
public class pet {
	
	String nome;
	String tipo;
	String sexo;
	String endereco;
	Integer idade;
	Double peso;
	String raca;
	
	

	public pet() {
		// TODO Auto-generated constructor stub
	}

	public void setNome(String resposta) {
		this.nome = resposta;	
	}
	
	public void setTipo(String resposta) {
		this.tipo = resposta;
	}
	
	public void setSexo(String resposta) {
		this.sexo = resposta;
	}
	
	public void setEndereco(String resposta) {
		this.endereco = resposta;
	}
	
	public void setIdade(Integer resposta) {
		this.idade = resposta;
	}
	
	public void setPeso(Double resposta) {
		this.peso = resposta;
	}
	
	public void setRaca(String resposta) {
		this.raca = resposta;
	}

}
